$SMTPServer = "smtp.gmail.com";$SMTPPort = "587";$Username = "";$Password = "";$to = "";$subject = "logs";$body = "Kindly find logs attached";$attachment = ".\result.zip";$message = New-Object System.Net.Mail.MailMessage;$message.subject = $subject;$message.body = $body;$message.to.add($to);$message.from = $username;$message.attachments.add($attachment);$smtp = New-Object System.Net.Mail.SmtpClient($SMTPServer, $SMTPPort);$smtp.EnableSSL = $true;$smtp.Credentials = New-Object System.Net.NetworkCredential($Username, $Password);$smtp.send($message);